<?	$mysqli = new mysqli("localhost","root", "paintFRAME!", "scrabble");$result = $mysqli->query("SELECT * FROM  `data` order by `time` desc limit 1");$word="";while ($row = $result->fetch_assoc())	{		$word=$row['word'];	}//echo $word;$mysqli = new mysqli("localhost","root", "paintFRAME!", "words");$result = $mysqli->query("SELECT * FROM  `all_words` WHERE `stimuli` =  '".$word."' order by `word` ASC");while ($row = $result->fetch_assoc())	{		$i=0;		if($row['ngram_value']*1000000000>1000)		{			echo $row['word'] . " ";			if($i==10)			{				echo "<br>";				$i=0;			}			$i++;		}	}	echo "<div style='clear:both'></div>";?>